import React, { Component } from 'react';
import axios from 'axios';

export default class Card extends Component {

   state = {
        styles: [],
        locations: [],
        selectedStyle: {},
        selectedLocation: {}
    }

    componentDidMount() {

        axios.get(`http://eat.local/wp-json/ijab/v1/options/`)
        .then(res => {
            this.setState({
                styles: res.data.styles,
                locations: res.data.locations
            })
        })
        .catch(error => {
            console.log( 'Error fetching and parsing data: ', error );
        });

    }

    handleSubmit = ( event ) => {

        event.preventDefault();

        this.setState({
            selectedStyle: event.target.styles.value,
            selectedLocation: event.target.locations.value
        })
    }


    render() {
        let styleList = this.state.styles.map(
            style => <option key={ style.term_id } value={ style.slug }>
                { style.name }
            </option>
        );
        let locationList = this.state.locations.map(
            location => <option key={ location.term_id } value={ location.slug }>
                { location.name }
            </option>
        );

        return (

            <div className="card">

                <form className="card" onSubmit={ this.handleSubmit }>

                    <div className="selector">
                        <label>Select a style:
                            <select name="styles" id="styles" value={ this.state.style }>{ styleList }</select>
                        </label>
                    </div>

                    <div className="selector">
                        <label>Select a location:
                            <select name="locations" id="locations" value={ this.state.location }>{ locationList }</select>
                        </label>
                    </div>

                    <button type="submit" id="submit" className="accent">Load</button>

                </form>

                <div className="results">
                    <p>Your destination!</p>
                </div>

            </div>

        );

    }

}
