import React, { Component } from 'react';
import axios from 'axios';

export default class Card extends Component {

   state = {
        restaurants: []
    }

    componentDidMount() {
        axios.get(`./restaurants.json`)
          .then(res => {
            const restaurants = res.data;
            this.setState({ restaurants });
          })
          .catch(error => {
            console.log('Error fetching and parsing data', error);
          });
      }

    render() {

        let list = this.state.restaurants.map(
            restaurant => <div key={restaurant.id}>
                <h4>{restaurant.name}</h4>
                <span>Area: {restaurant.area}</span>
                <span>Style: {restaurant.style}</span>
            </div>
        );

        return (
            <div className="card">
            <p>Use our handy prediction app to find the perfect spot for supper.</p>
            <div>
                { list }
            </div>
            <button className="accent">Load</button>
        </div>
        );

    }

}
