import React, { Component } from 'react';
import axios from 'axios';

export default class Card extends Component {

   state = {
        restaurants: [],
        styles: [],
        locations: [],
        options: {
            styles: [],
            locations: []
        }
    }

    componentDidMount() {
        axios.get(`./restaurants.json`)
          .then(res => {
            const restaurants = res.data;
            this.setState({ restaurants });
          })
          .catch(error => {
            console.log('Error fetching and parsing data', error);
          });

        axios.get(`http://eat.local/wp-json/ijab/v1/options/`)
          .then(res => {
            const options = res.data;
            this.setState({ options });
            const styles = res.data.styles;
            const locations = res.data.locations;
            this.setState({styles});
            this.setState({locations});
            console.log({ styles });
          })
          .catch(error => {
            console.log('Error fetching and parsing data', error);
          });

      }

      render() {

        let list = this.state.styles.map(
            style => <div key={ style.term_id }>
                <h4>{style.name}</h4>
            </div>
        );

        return (
            <div className="card">
            <p>Use our handy prediction app to find the perfect spot for supper.</p>
            <div>
              <list />
            </div>
            <button className="accent">Load</button>
        </div>
        );

    }

}
