import React, { Component } from 'react';
import axios from 'axios';

export default class Card extends Component {

   state = {
        restaurants: [],
        styles: [],
        locations: []
    }

    componentDidMount() {
        axios.get(`./restaurants.json`)
          .then(res => {
            const restaurants = res.data;
            this.setState({ restaurants });
          })
          .catch(error => {
            console.log('Error fetching and parsing data', error);
          });

        axios.get(`http://eat.local/wp-json/ijab/v1/options/`)
          .then(res => {
            this.setState({
                styles: res.data.styles,
                locations: res.data.locations
            })
          })
          .catch(error => {
            console.log('Error fetching and parsing data', error);
          });

      }

      render() {
        let list = this.state.styles.map(
            style => <option key={ style.term_id } value={ style.slug }>
                { style.name }
            </option>
        );

        return (

            <div className="card">
            <label for="styles">Select a style</label>

             <select name="style" id="styles">{ list }</select>

            <button className="accent">Load</button>

        </div>
        );

    }

}
