import React, { Component } from 'react';
import axios from 'axios';

export default class Card extends Component {
   state = {
            restaurants: []
        }

    componentDidMount() {
        axios.get(`restaurants.json`)
        .then(response => {
          this.setState({
            restaurants: response.data.data
          });
        })
        .catch(error => {
          console.log('Error fetching and parsing data', error);
        });
    }
    render() {
        let display = this.restaurants.map(place =>
            <p>{place.name}</p>
          );
        return (
            <div className="card">
            <p>Use our handy prediction app to find the perfect spot for supper.</p>
            {display}
            <button className="accent">Load</button>
        </div>
        );
    }

}
